// NSArrayExtras.h -- extra methods for NSArray// by allen brunson  july 12 2010/******************************************************************************//*                                                                            *//***  NSArray inline functions                                              ***//*                                                                            *//******************************************************************************/NS_INLINE BOOL VGIsArray(NSArray* array){    if (!array) return FALSE;        if ([array isKindOfClass:[NSArray class]])        return TRUE;    if ([array isKindOfClass:[NSMutableArray class]]) return TRUE;        return FALSE;}NS_INLINE BOOL VGArrayHasObjects(NSArray* array){    return VGIsArray(array) && array.count >= 1;}NS_INLINE BOOL VGArrayIndexValid(NSArray* array, NSInteger indx){    return VGIsArray(array) && (indx >= 0) && (indx < (NSInteger)array.count);}NS_INLINE NSArray* VGDupeArray(NSArray* list){    NSArray*  copy = nil;        if (list == nil) return [NSArray array];        copy = [NSArray alloc];    copy = [copy initWithArray:list copyItems:TRUE];    copy = [copy autorelease];        return copy;}/******************************************************************************//*                                                                            *//***  NSArrayExtras category                                                ***//*                                                                            *//******************************************************************************/@interface NSArray (NSArrayExtras)-(BOOL)empty;-(id)firstObject;-(BOOL)hasObjects;@end/******************************************************************************//*                                                                            *//***  NSMutableArrayExtras category                                         ***//*                                                                            *//******************************************************************************/@interface NSMutableArray (NSMutableArrayExtras)-(NSArray*)removeObjects:(NSUInteger)count;@end/******************************************************************************//*                                                                            *//***  NSArrayExtras category                                                ***//*                                                                            *//******************************************************************************overview--------few extra methods for NSArray*/