// NSArrayExtras.m -- extra methods for NSArray// by allen brunson  july 12 2010#import "NSArrayExtras.h"@implementation NSArray (NSArrayExtras)-(BOOL)empty{    return self.count <= 0;}-(id)firstObject{    if (self.hasObjects)    {        return [self objectAtIndex:0];    }    else    {        return nil;    }}-(BOOL)hasObjects{    return self.count >= 1;}@end@implementation NSMutableArray (NSMutableArrayExtras)// remove up to 'count' objects from the beginning of this array-(NSArray*)removeObjects:(NSUInteger)count{    NSArray*       list = nil;    const NSRange  rang = NSMakeRange(0, MIN(count, self.count));        if (rang.length >= 1)    {        list = [self subarrayWithRange:rang];        [self removeObjectsInArray:list];    }    else    {        list = [NSArray array];    }        return list;}@end